    data_file = os.path.join(data_dir, 'process.csv')

    seed =0
    random.seed(seed)
    np.random.seed(seed)

    data = pd.read_csv(data_file)
    df_train, temp = train_test_split(data, test_size=0.2, random_state=seed)
    df_val, df_test = train_test_split(temp, test_size=0.5, random_state=seed)

    drugs = dict()
    proteins = dict()
    data['ISO_SMILES'] = ""
    data['LID'] = ""
    data['PID'] = ""
    data['train'] = 0
    data['val'] = 0
    data['test'] = 0
    smiles_map = {}
    target_key2id = {}
    for idx, row in tqdm(data.iterrows(), total=len(data)):
        mol = Chem.MolFromSmiles(row['Drug'])
        smiles = Chem.MolToSmiles(mol, isomericSmiles=True)
        LID = drugs.get(smiles, len(drugs))
        drugs[smiles] = LID
        smiles_map[row['Drug']] = smiles
        PID = proteins.get(row['Target'], len(proteins))
        proteins[row['Target']] = PID
        target_key2id[row['target_key']] = PID

        data.loc[idx, 'ISO_SMILES'] = smiles
        data.loc[idx, 'LID'] = LID
        data.loc[idx, 'PID'] = PID


    data.loc[df_train.index, 'train'] = 1
    data.loc[df_val.index, 'val'] = 1
    data.loc[df_test.index, 'test'] = 1

    print("protein:",len(proteins), "ligand:", len(drugs), "affinity:", len(data))
    os.makedirs(save_dir, exist_ok=True)
    if data_dir.endswith("KIBA"):
        data.to_csv(os.path.join(save_dir, 'process.csv'))
