    import h5py
    import torch
    import pickle
    graph_mvp_file = os.path.join(data_dir, "GraphMVP_features.h5")
    morgan_file = os.path.join(data_dir, 'Morgan_features.h5')
    prot_bert_file = os.path.join(data_dir, 'ProtBert_features.h5')
    pocket_file = os.path.join(data_dir, "Domain_coord_graph_top3seqid_dict.pickle")

    inputs_embeds = []
    with h5py.File(graph_mvp_file, "r") as h5fi:
        for seq in drugs:
            assert seq in h5fi.keys()
            inputs_embeds.append(h5fi[seq][:])
    inputs_embeds = np.stack(inputs_embeds)
    inputs_embeds = torch.from_numpy(inputs_embeds)

    embedding = []
    with h5py.File(morgan_file, "r") as h5fi:
        for seq in drugs:
            assert seq in h5fi.keys()
            embedding.append(h5fi[seq][:])
    embedding = np.stack(embedding)
    embedding = torch.from_numpy(embedding)

    with open(os.path.join(save_dir, "ligand_features.pickle"), 'wb') as f:
        pickle.dump({"ligand_embedding": embedding,
                     "drugs": drugs,
                     "ligand_inputs_embeds": inputs_embeds}, f)

    embedding = []
    with h5py.File(prot_bert_file, "r") as h5fi:
        for seq in proteins:
            assert seq in h5fi.keys()
            embedding.append(h5fi[seq][:])
        embedding = np.stack(embedding)
        embedding = torch.from_numpy(embedding)
    from torch_geometric.data import Data
    with open(pocket_file, 'rb') as f:
        pocket_data = pickle.load(f)

    ans = np.empty(len(pocket_data), dtype=object)
    for key, i in target_key2id.items():
        pocket_data[key].node_features = pocket_data[key].x
        ans[i] = pocket_data[key].cpu()

    with open(os.path.join(save_dir, "protein_features.pickle"), 'wb') as f:
        pickle.dump({"protein_embedding": embedding,
                     "protein_graph": ans}, f)







